import React, {useContext} from 'react';
import ReactDOM from 'react-dom';

const MyContext = React.createContext();


const App = () => {
    return (
        <MyContext.Provider value = "Hello world">
            <Child/>
        </MyContext.Provider>
    );
};

const Child = () => {
    return (
        <MyContext.Consumer>
            {(value) => {
                return (
                    <p> {value} </p>
                )
            }}
        </MyContext.Consumer>
    );
}








ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
